graphBounds._queryString:
{[sym;exchange;depth]
   tab:queryLastBook[sym;exchange;depth];
   midPrice:avg value exec first bids,first asks from tab;
   minVal:exec last bids from tab;
   maxVal:exec last asks from tab;
   x:max(abs midPrice-minVal;abs midPrice-maxVal);
   midSize:max raze exec bidsizes,asksizes from tab;
   `minValue`maxValue`midPrice`midSize!(midPrice-x;midPrice+x;midPrice;midSize)
  }

depthPercent._queryString:
{[sym;exchange;percent]
  emptyState:enlist `lowerBound`upperBound`sizeToBuy`sizeToSell!();
  if[null percent; percent:0];
  tab:queryLastBook[sym;exchange;25];
  $[99h ~ abs type tab;
    :emptyState;
    [mid:avg exec first bids, first asks from tab;
      bounds:`lowerBound`upperBound!((mid*1-percent%100);(mid*1+percent%100));
      tab:delete from tab where bids<bounds[`lowerBound], asks> bounds[`upperBound];
      tmp:enlist bounds,`sizeToBuy`sizeToSell!
        ((sum tab[`bidsizes]);
        (sum tab[`asksizes]))
        ]
  ]

  }

